window.addEventListener("load",()=>{const e=new KeenSlider("#slider-use-cases",{loop:!0,created:e=>{const t=e.container.nextElementSibling,n=t.querySelector(".arrow-left"),s=t.querySelector(".arrow-right");n.addEventListener("click",()=>e.prev()),s.addEventListener("click",()=>e.next())}})}),window.addEventListener("scroll",()=>{if(window.pageYOffset>20){const e=document.querySelector(".header-content");e.classList.contains("scrolled")||e.classList.add("scrolled")}else{const e=document.querySelector(".header-content");e.classList.contains("scrolled")&&e.classList.remove("scrolled")}})